var pools = require("pool");

module.exports = {
    Activate: function (ability, x, y, direction, emitter) {
        switch (ability) {
            case 'ROCKET':
                pools.Rocket.ActivateProjectile(x, y, direction, emitter);
                break;
            case 'MINE':
                pools.Mine.ActivateProjectile(x, y, emitter);
                break;
            default:
        }
    }    
};